    public void DrawPicture(int x, int y, string txt, string color)
    {
        string[] txtData = txt.Split('\n');
        for (int i = 0; i < txtData.Length; i++)
        {
            Program.ui.DrawText(x, y + i, txtData[i], color);
        }
    }
